// SPDX-License-Identifier: MIT
pragma solidity ^0.6.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";

contract XD is
    Ownable,
    ERC20Burnable,
    ERC20Pausable
{
    using SafeMath for uint256;

    constructor()
        public
        ERC20("Lol ale padaka", "XD")
    {
        uint256 INITIAL_SUPPLY = 9_999_997 * (10 ** 18);
        _mint(_msgSender(), INITIAL_SUPPLY);
    }

    /**
     * @dev Creates `amount` new tokens for `to`.
     *
     * See {ERC20-_mint}.
     *
     * Requirements:
     * - a caller must be the owner
     */
    function mint(address to, uint256 amount) external onlyOwner {
        _mint(to, amount);
    }

    /**
     * @dev Pauses all token transfers.
     *
     * See {ERC20Pausable} and {Pausable-_pause}.
     *
     * Requirements
     * - a caller must be the owner
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpauses all token transfers.
     *
     * See {ERC20Pausable} and {Pausable-_unpause}.
     *
     * Requirements:
     * - a caller must be the owner
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    function _beforeTokenTransfer(address from, address to, uint256 amount) internal override(ERC20, ERC20Pausable) {
        super._beforeTokenTransfer(from, to, amount);
    }

}

// SPDX-License-Identifier: MIT
pragma solidity ^0.6.0;

import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";

contract TokenClaimer is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    IERC20 public token;
    uint256 private immutable _startMonth;
    uint256 public immutable startDate;
    uint256 public immutable endDate;
    uint256 public immutable claimAmount;
    uint256 public immutable burnAmount;

    uint256 public totalClaimed;
    uint256 public totalToBurn;

    mapping(address => uint256) private _lastClaimMonth;

    constructor(
        address token_,
        uint256 startMonth_,
        uint256 monthsValid_
    ) public {
        token = IERC20(token_);

        _startMonth = startMonth_;
        (startDate, endDate) = calculateTimeBounds(startMonth_, monthsValid_);

        claimAmount = 70 * (10**18);
        burnAmount = 30 * (10**18);
    }

    /**
     * Returns the current balance of the user. If it's not registered we return 0.
     */
    function balanceOf(address account) public view returns (uint256) {
        if (!isClaimer(account)) {
            return 0;
        }

        if (!isClaimingOpen()) {
            return 0;
        }

        uint256 elapsedMonths = getMonthsSinceLastClaim(account);

        if (elapsedMonths > 0) {
            return (claimAmount + burnAmount);
        } else {
            return 0;
        }
    }

    /**
     * We determine if a user is registered with our contract by checking last value of claiming.
     * It is equal or bigger than CALENDAR_MARK and 0 by default for unset.
     */
    function isClaimer(address account) public view returns (bool) {
        return _lastClaimMonth[account] != 0;
    }

    /**
     * Adds users allowed for periodic claiming.
     */
    function addClaimers(address[] memory addresses) external onlyOwner {
        for (uint256 i = 0; i < addresses.length; i++) {
            _lastClaimMonth[addresses[i]] = CALENDAR_MARK;
        }
    }

    function isClaimingOpen() public view returns (bool) {
        return (startDate < now && now < endDate);
    }

    /**
     * Allows user to claim his tokens once per month, with no accumulation.
     */
    function claimMonthly() external nonReentrant {
        address account = _msgSender();
        require(
            isClaimer(account),
            "Address hasn't been added to the list of claimers."
        );

        require(isClaimingOpen(), "Claiming period has ended.");

        uint256 elapsedMonths = getMonthsSinceLastClaim(account);
        require(
            elapsedMonths > 0,
            "You've already claimed your tokens for this month!"
        );

        uint256 currentMonthOrd = getMonthOrdinal(now);
        _lastClaimMonth[account] = currentMonthOrd;

        claim(claimAmount);
        updateBurn(burnAmount);

        require(getMonthsSinceLastClaim(account) == 0);
        require(balanceOf(account) == 0);
    }

    function claim(uint256 tokens) private {
        totalClaimed = totalClaimed + tokens;

        token.safeTransferFrom(owner(), _msgSender(), tokens);
        // if the transfer fails for some reason (ex. out of gas) then the whole transaction fails and we don't zero out the balance
    }

    function updateBurn(uint256 tokens) private {
        totalToBurn = totalToBurn + tokens;
        // burning is performed separately by the client
    }

    function getMonthsSinceLastClaim(address account)
        private
        view
        returns (uint256)
    {
        uint256 lastClaim = _lastClaimMonth[account];
        uint256 currentMonth = getMonthOrdinal(now);

        uint256 monthsElapsed = currentMonth - lastClaim;

        return monthsElapsed;
    }

    /**
     * Calculates start and end timestamps, which will be the first days at midnight of appropriate months.
     */
    function calculateTimeBounds(uint256 startMonth, uint256 monthsValid)
        public
        pure
        returns (uint256, uint256)
    {
        require(0 < startMonth, "Calendar numbers months starting with 1.");
        require(
            startMonth < 24 - monthsValid,
            "Calendar expires after 2 years."
        );

        uint256 startTimestamp = getMonthIdxReverse(startMonth);
        uint256 endTimestamp = getMonthIdxReverse(startMonth + monthsValid);

        return (startTimestamp, endTimestamp);
    }

    // Calendar utils

    uint256 constant CALENDAR_MARK = 0x859; // must be anything but non-zero

    // all times represent beginning of a new month in GMT+0 time
    uint256 constant JANUARY_2021 = 1609455600;
    uint256 constant FEBRUARY_2021 = 1612134000;
    uint256 constant MARCH_2021 = 1614553200;
    uint256 constant APRIL_2021 = 1617228000;
    uint256 constant MAY_2021 = 1619820000;
    uint256 constant JUNE_2021 = 1622498400;
    uint256 constant JULY_2021 = 1625090400;
    uint256 constant AUGUST_2021 = 1627768800;
    uint256 constant SEPTEMBER_2021 = 1630447200;
    uint256 constant OCTOBER_2021 = 1633039200;
    uint256 constant NOVEMBER_2021 = 1635721200;
    uint256 constant DECEMBER_2021 = 1638313200;
    uint256 constant JANUARY_2022 = 1640991600;
    uint256 constant FEBRUARY_2022 = 1643670000;
    uint256 constant MARCH_2022 = 1646089200;
    uint256 constant APRIL_2022 = 1648764000;
    uint256 constant MAY_2022 = 1651356000;
    uint256 constant JUNE_2022 = 1654034400;
    uint256 constant JULY_2022 = 1656626400;
    uint256 constant AUGUST_2022 = 1659304800;
    uint256 constant SEPTEMBER_2022 = 1661983200;
    uint256 constant OCTOBER_2022 = 1664575200;
    uint256 constant NOVEMBER_2022 = 1667257200;
    uint256 constant DECEMBER_2022 = 1669849200;

    function getMonth(uint256 timestamp) public pure returns (uint256) {
        uint256[24] memory months_in_order = [
            JANUARY_2021,
            FEBRUARY_2021,
            MARCH_2021,
            APRIL_2021,
            MAY_2021,
            JUNE_2021,
            JULY_2021,
            AUGUST_2021,
            SEPTEMBER_2021,
            OCTOBER_2021,
            NOVEMBER_2021,
            DECEMBER_2021,
            JANUARY_2022,
            FEBRUARY_2022,
            MARCH_2022,
            APRIL_2022,
            MAY_2022,
            JUNE_2022,
            JULY_2022,
            AUGUST_2022,
            SEPTEMBER_2022,
            OCTOBER_2022,
            NOVEMBER_2022,
            DECEMBER_2022
        ];
        uint256 effective_month = 0;
        for (
            uint256 month_idx = 0;
            month_idx < months_in_order.length;
            month_idx++
        ) {
            if (timestamp < months_in_order[month_idx]) {
                return effective_month;
            } else {
                effective_month = (effective_month % 12) + 1;
            }
        }

        return effective_month;
    }

    function getMonthIdx(uint256 timestamp) public pure returns (uint256) {
        uint256[24] memory months_in_order = [
            JANUARY_2021,
            FEBRUARY_2021,
            MARCH_2021,
            APRIL_2021,
            MAY_2021,
            JUNE_2021,
            JULY_2021,
            AUGUST_2021,
            SEPTEMBER_2021,
            OCTOBER_2021,
            NOVEMBER_2021,
            DECEMBER_2021,
            JANUARY_2022,
            FEBRUARY_2022,
            MARCH_2022,
            APRIL_2022,
            MAY_2022,
            JUNE_2022,
            JULY_2022,
            AUGUST_2022,
            SEPTEMBER_2022,
            OCTOBER_2022,
            NOVEMBER_2022,
            DECEMBER_2022
        ];
        uint256 effective_idx = 0;
        for (
            uint256 month_idx = 0;
            month_idx < months_in_order.length;
            month_idx++
        ) {
            if (timestamp < months_in_order[month_idx]) {
                return effective_idx;
            } else {
                effective_idx = effective_idx + 1;
            }
        }

        return effective_idx;
    }

    function getMonthIdxReverse(uint256 month_idx)
        public
        pure
        returns (uint256)
    {
        uint256[24] memory months_in_order = [
            JANUARY_2021,
            FEBRUARY_2021,
            MARCH_2021,
            APRIL_2021,
            MAY_2021,
            JUNE_2021,
            JULY_2021,
            AUGUST_2021,
            SEPTEMBER_2021,
            OCTOBER_2021,
            NOVEMBER_2021,
            DECEMBER_2021,
            JANUARY_2022,
            FEBRUARY_2022,
            MARCH_2022,
            APRIL_2022,
            MAY_2022,
            JUNE_2022,
            JULY_2022,
            AUGUST_2022,
            SEPTEMBER_2022,
            OCTOBER_2022,
            NOVEMBER_2022,
            DECEMBER_2022
        ];
        require(0 < month_idx, "Index too low.");
        require(month_idx < 25, "Index too high.");
        return months_in_order[month_idx - 1];
    }

    /**
     * Returns a number that can be used for month comparing and delta calculation
     */
    function getMonthOrdinal(uint256 timestamp) public pure returns (uint256) {
        uint256[24] memory months_in_order = [
            JANUARY_2021,
            FEBRUARY_2021,
            MARCH_2021,
            APRIL_2021,
            MAY_2021,
            JUNE_2021,
            JULY_2021,
            AUGUST_2021,
            SEPTEMBER_2021,
            OCTOBER_2021,
            NOVEMBER_2021,
            DECEMBER_2021,
            JANUARY_2022,
            FEBRUARY_2022,
            MARCH_2022,
            APRIL_2022,
            MAY_2022,
            JUNE_2022,
            JULY_2022,
            AUGUST_2022,
            SEPTEMBER_2022,
            OCTOBER_2022,
            NOVEMBER_2022,
            DECEMBER_2022
        ];
        uint256 ordinal = CALENDAR_MARK;
        for (
            uint256 month_idx = 0;
            month_idx < months_in_order.length;
            month_idx++
        ) {
            if (timestamp < months_in_order[month_idx]) {
                return ordinal;
            } else {
                ordinal = ordinal + 1;
            }
        }
        return ordinal;
    }
}
